from django import forms

from .models import User

log_name = {
    "type":"text", 
    "name":"your_name",
    "id":"your_name",
    "placeholder":"Nom"
}

log_pass = {
    "type":"password", 
    "name":"your_pass",
    "id":"your_pass",
    "placeholder":"Mot de passe"
}

sign_name = {
    "type":"text",
    "name":"name", 
    "id":"name", 
    "placeholder":"Nom"
}

sign_mail = {
    "type":"email",
    "name":"email", 
    "id":"email", 
    "placeholder":"Email"
}

sign_pass = {
    "type":"password",
    "name":"pass", 
    "id":"pass", 
    "placeholder":"Mot de passe"
}

sign_repass = {
    "type":"password",
    "name":"re_pass", 
    "id":"re_pass", 
    "placeholder":"Confirmer le mot de passe"
}

class SignUpForm(forms.ModelForm) :
    
    class Meta :
        model = User
        fields = ['username', 'email', 'password', 'password1']
        widgets = {
            'password' : forms.PasswordInput(attrs={'autocomplete': 'off'}),
            'password1' : forms.PasswordInput(attrs={'autocomplete': 'off'})
        }
        
    def __init__(self, *args, **kwargs): 
        super().__init__(*args, **kwargs)
        self.fields['username'].widget.attrs.update(sign_name)
        self.fields['email'].widget.attrs.update(sign_mail)
        self.fields['password'].widget.attrs.update(sign_pass)
        self.fields['password1'].widget.attrs.update(sign_repass)
        
        
class LoginForm(forms.ModelForm):

    class Meta:
        model = User
        fields = ['username','password']
        widgets = {
            'password' : forms.PasswordInput(attrs={'autocomplete': 'off'}),
        }
    
    
    def __init__(self, *args, **kwargs): 
        super().__init__(*args, **kwargs)
        self.fields['username'].widget.attrs.update(log_name)
        self.fields['password'].widget.attrs.update(log_pass)