import requests
key = "da299ac705134448bb492c93846fecf3"

api_address = "http://newsapi.org/v2/top-headlines?country=civ&apiKey="+key
json_data = requests.get(api_address).json()

ar=[]

def news():
    for i in range(len(json_data["articles"])):
        ar.append("Number "+str(i+1)+". " + json_data["articles"][i]["title"]+".")
        
    return ar

arr=news()

print(arr)