from selenium import webdriver
import time

class infow():
    def __init__(self):
        # Specify the path to the downloaded chromedriver.exe
        #chrome_driver_path = r'C:\Users\HanSAriEle\Downloads\chromedriver_win32\chromedriver.exe'
        self.driver = webdriver.Chrome()
        #self.driver.quit()
        
    def get_info(self, query):
        self.query = query
        self.driver.get(url='https://www.wikipedia.org/')
        search = self.driver.find_element("xpath", '//*[@id="searchInput"]')
        search.click()
        search.send_keys(query)
        #enter = self.driver.find_element()//*[@id="search-form"]/fieldset/button
        enter = self.driver.find_element("xpath", '//*[@id="search-form"]/fieldset/button/i')
        enter.click()
        time.sleep(7200)
        self.driver.quit()



